mapper.select:
SELECT
c.customer_id,
c.customer_name,
c.phone_number,
SUM(csp.remaining_count) AS total_remaining_count
FROM customer c
JOIN customer_session_pass csp ON c.customer_id = csp.customer_id
WHERE c.shop_id = #{shopId}
AND csp.expiration_date IS NOT NULL

    AND csp.expiration_date >= #{request.startDate}


    AND csp.expiration_date <= #{request.endDate}

GROUP BY c.customer_id, c.customer_name, c.phone_number

    HAVING SUM(csp.remaining_count) >= #{request.minRemainingCount}




            AND SUM(csp.remaining_count) <= #{request.maxRemainingCount}


            HAVING SUM(csp.remaining_count) <= #{request.maxRemainingCount}



ORDER BY MIN(csp.expiration_date) ASC
LIMIT #{offset}, #{request.size}

SELECT COUNT(*) FROM (
SELECT c.customer_id
FROM customer c
JOIN customer_session_pass csp ON c.customer_id = csp.customer_id
WHERE c.shop_id = #{shopId}
AND csp.expiration_date IS NOT NULL

    AND csp.expiration_date >= #{request.startDate}


    AND csp.expiration_date <= #{request.endDate}

GROUP BY c.customer_id

    HAVING SUM(csp.remaining_count) >= #{request.minRemainingCount}




            AND SUM(csp.remaining_count) <= #{request.maxRemainingCount}


            HAVING SUM(csp.remaining_count) <= #{request.maxRemainingCount}



) AS filtered
