choose.when:
SELECT r.reservation_id AS id, 'RESERVATION' AS scheduleType, c.customer_name AS title, r.reservation_start_at AS startAt, r.reservation_end_at AS endAt, s.staff_name AS staffName, r.reservation_memo AS memo, r.reservation_status_name AS status, GROUP_CONCAT(DISTINCT si.secondary_item_name ORDER BY si.secondary_item_name SEPARATOR ', ') AS items, s.color_code AS staffColor, c.customer_name AS customerName FROM reservation r JOIN staff s ON r.staff_id = s.staff_id JOIN customer c ON r.customer_id = c.customer_id LEFT JOIN reservation_detail rd ON r.reservation_id = rd.reservation_id LEFT JOIN secondary_item si ON rd.secondary_item_id = si.secondary_item_id WHERE r.deleted_at IS NULL AND s.shop_id = #{shopId}
AND r.reservation_start_at BETWEEN #{request.from} AND #{request.to}
AND (c.customer_name LIKE CONCAT('%', #{request.customerKeyword}, '%') OR c.customer_phone LIKE CONCAT('%', #{request.customerKeyword}, '%'))
AND si.secondary_item_name LIKE CONCAT('%', #{itemKeyword}, '%')
AND s.staff_id = #{request.staffId}
GROUP BY r.reservation_id ORDER BY startAt ASC

( SELECT p.plan_id AS id, 'PLAN' AS scheduleType, p.plan_title AS title, p.plan_start_at AS startAt, p.plan_end_at AS endAt, s.staff_name AS staffName, p.plan_memo AS memo, NULL AS status, NULL AS items, s.color_code AS staffColor, NULL AS customerName FROM plan p JOIN staff s ON p.staff_id = s.staff_id WHERE s.shop_id = #{shopId}
AND p.plan_start_at BETWEEN #{request.from} AND #{request.to}
) ORDER BY startAt ASC

( SELECT l.leave_id AS id, 'LEAVE' AS scheduleType, l.leave_title AS title, l.leave_at AS startAt, l.leave_at AS endAt, s.staff_name AS staffName, l.leave_memo AS memo, NULL AS status, NULL AS items, s.color_code AS staffColor, NULL AS customerName FROM `leave` l JOIN staff s ON l.staff_id = s.staff_id WHERE s.shop_id = #{shopId}
AND l.leave_at BETWEEN #{request.from} AND #{request.to}
) ORDER BY startAt ASC

choose.otherwise:
( SELECT r.reservation_id AS id, 'RESERVATION' AS scheduleType, c.customer_name AS title, r.reservation_start_at AS startAt, r.reservation_end_at AS endAt, s.staff_name AS staffName, r.reservation_memo AS memo, r.reservation_status_name AS status, GROUP_CONCAT(DISTINCT si.secondary_item_name ORDER BY si.secondary_item_name SEPARATOR ', ') AS items, s.color_code AS staffColor, c.customer_name AS customerName FROM reservation r JOIN staff s ON r.staff_id = s.staff_id JOIN customer c ON r.customer_id = c.customer_id LEFT JOIN reservation_detail rd ON r.reservation_id = rd.reservation_id LEFT JOIN secondary_item si ON rd.secondary_item_id = si.secondary_item_id WHERE r.deleted_at IS NULL AND s.shop_id = #{shopId}
AND r.reservation_start_at BETWEEN #{request.from} AND #{request.to}
AND s.staff_id = #{request.staffId}
GROUP BY r.reservation_id
UNION ALL
SELECT p.plan_id AS id, 'PLAN' AS scheduleType, p.plan_title AS title, p.plan_start_at AS startAt, p.plan_end_at AS endAt, s.staff_name AS staffName, p.plan_memo AS memo, NULL AS status, NULL AS items, s.color_code AS staffColor, NULL AS customerName FROM plan p JOIN staff s ON p.staff_id = s.staff_id WHERE s.shop_id = #{shopId}
AND p.plan_start_at BETWEEN #{request.from} AND #{request.to}
AND s.staff_id = #{request.staffId}
UNION ALL
SELECT l.leave_id AS id, 'LEAVE' AS scheduleType, l.leave_title AS title, l.leave_at AS startAt, l.leave_at AS endAt, s.staff_name AS staffName, l.leave_memo AS memo, NULL AS status, NULL AS items, s.color_code AS staffColor, NULL AS customerName FROM `leave` l JOIN staff s ON l.staff_id = s.staff_id WHERE s.shop_id = #{shopId}
AND l.leave_at BETWEEN #{request.from} AND #{request.to}
AND s.staff_id = #{request.staffId}
) ORDER BY startAt ASC
