mapper.select:
SELECT r.reservation_id AS reservationId, c.customer_name AS customerName, c.phone_number AS customerPhone, r.reservation_start_at AS reservationStartAt, r.reservation_end_at AS reservationEndAt, GROUP_CONCAT(si.secondary_item_name SEPARATOR ', ') AS itemNames, s.staff_name AS staffName, r.reservation_status_name AS reservationStatusName, r.staff_memo AS staffMemo, r.reservation_memo AS reservationMemo FROM reservation r LEFT JOIN customer c ON r.customer_id = c.customer_id LEFT JOIN staff s ON r.staff_id = s.staff_id LEFT JOIN reservation_detail rd ON r.reservation_id = rd.reservation_id LEFT JOIN secondary_item si ON rd.secondary_item_id = si.secondary_item_id WHERE r.shop_id = #{req.shopId} AND r.deleted_at IS NULL
AND r.staff_id = #{req.staffId}
AND r.reservation_status_name = #{req.reservationStatusName}
AND (c.customer_name LIKE CONCAT('%', #{req.customerKeyword}, '%') OR c.phone_number LIKE CONCAT('%', #{req.customerKeyword}, '%'))
AND r.reservation_start_at BETWEEN #{req.from} AND #{req.to}
GROUP BY r.reservation_id ORDER BY r.reservation_start_at ASC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(DISTINCT r.reservation_id)
FROM reservation r
LEFT JOIN customer c ON r.customer_id = c.customer_id
WHERE r.deleted_at IS NULL

    AND r.shop_id = #{req.shopId}


    AND r.staff_id = #{req.staffId}


    AND r.reservation_status_name = #{req.reservationStatusName}


    AND (
    c.customer_name LIKE CONCAT('%', #{req.customerKeyword}, '%')
    OR c.phone_number LIKE CONCAT('%', #{req.customerKeyword}, '%')
    )


    AND r.reservation_start_at BETWEEN #{req.from} AND #{req.to}
